package com.eviro.assessment.grad001.ManqobaLubisi.serviceImpl;

import com.eviro.assessment.grad001.ManqobaLubisi.entity.AccountProfile;
import com.eviro.assessment.grad001.ManqobaLubisi.repository.AccountProfileRepository;
import com.univocity.parsers.common.record.Record;
import com.univocity.parsers.csv.CsvParser;
import com.univocity.parsers.csv.CsvParserSettings;
import java.io.File;
import java.io.FileInputStream;
import java.io.FileNotFoundException;
import java.io.InputStreamReader;
import java.io.Reader;
import java.io.UnsupportedEncodingException;
import java.net.URL;
import java.util.ArrayList;
import java.util.List;
import org.assertj.core.api.Assertions;
import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.*;
import org.mockito.ArgumentCaptor;
import org.mockito.Mockito;
import static org.mockito.Mockito.verify;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.autoconfigure.orm.jpa.DataJpaTest;
import org.springframework.boot.test.context.SpringBootTest;


@SpringBootTest
class FileParserImpleTest {

    @Autowired
    private FileParserImple fileParserImple;
    @Autowired
    private AccountProfileRepository repository;

    @Test
    void parseCSV() throws Exception {
        //given
        File file = new File("CSV File/1664806859027-GraduateDev_AssessmentCsv_v2.csv");

        //when 
        fileParserImple.parseCSV(file);
        Reader inputReader = new InputStreamReader(new FileInputStream(file), "UTF-8");
        
        
        List<AccountProfile> accountList = new ArrayList<>();

        CsvParserSettings settings = new CsvParserSettings();
        //remove the headers of the file
        settings.setHeaderExtractionEnabled(true);
        settings.setMaxCharsPerColumn(-1);

        CsvParser csvParser = new CsvParser(settings);
        List<Record> recordList = csvParser.parseAllRecords(inputReader);
        for (Record record : recordList) {
            AccountProfile account = new AccountProfile();
            String imageFormat = record.getString("imageFormat");
            String imageData = record.getString("imageData");
            String imageFormatData = imageFormat + " " + imageData;

            File image = fileParserImple.convertCSVDataToImage(imageFormatData);

            URL url = fileParserImple.createImageLink(image);

            account.setName(record.getString("name"));
            account.setSurname(record.getString("surname"));
            account.setHttpImageLink(url.toString());

            accountList.add(account);
        }
        

        Assertions.assertThat(repository.saveAll(accountList)).isNotEmpty();

    }

    @Test
    void convertCSVDataToImage() {
        //given
        String based4 = "image/jpeg ";
        
        File file = fileParserImple.convertCSVDataToImage(based4);
        
        Assertions.assertThat(file).isFile();
        
    }
    

    @Test
    void createImageLink() {
        // given 
        File file = new File("/images/image.jpeg");
        
        //then
        URL url = fileParserImple.createImageLink(file);
        
        Assertions.assertThat(url).isNotNull();
    }
}
